import React from "react";
import SearchBar from "./SearchBar";
import VideoList from "./VideoList";
import YouTube from "../api/youtube_api";

class App extends React.Component {
	state = { videos: [] };

	onSearchTermSubmit = async searchTerm => {
		console.log(searchTerm);
		const response = await YouTube.get("/search", {
			params: {
				q: searchTerm
			}
		});
		this.setState({ videos: response.data.items });
		console.log(this.state.videos);
	};

	resultCount = () => {
		if (this.state.videos.length < 1) {
			return "Plese Enter a Search Term";
		} else {
			return <p>Found `${this.state.videos.length}` based on your search.</p>
		};
	}

	render() {
		return (
			<div className="ui container">
				<h3>YouTube API</h3>
				<SearchBar
					onSearchTermSubmit={this.onSearchTermSubmit}
				/>
				Found {this.state.videos.length} videos based on your search.
				<VideoList videos={this.state.videos} />
			</div>

		)
	}
}

export default App;
